{vb:set totalComments, 0}

{vb:set commentsPerPage, {vb:if "!empty($commentsPerPage)", {vb:raw commentsPerPage}, 25}}
{vb:set initialCommentsPerPage, {vb:if "!empty($initialCommentsPerPage)", {vb:raw initialCommentsPerPage}, 3}}

<vb:if condition="!empty($parentNodeIsBlog) AND !empty($page['pagenum'])">
	{vb:set pagenum, {vb:raw page.pagenum}}
<vb:else />
	{vb:set pagenum, 1}
</vb:if>
{vb:set scrollToCommentFlag, 0}
{vb:set hasComments, 0}
{vb:set sortOption.created, 'DESC'}
{vb:set options.sort, {vb:raw sortOption}}
{vb:set options.nolimit, 1}

<vb:if condition="isset($commentId) AND intval($commentId) > 0">
	{vb:data pagenum, search, getPageNumber, {vb:raw commentId}, {vb:raw conversation}, {vb:raw commentsPerPage}, 1, {vb:raw sortOption.created}, 0}
	<vb:if condition="$pagenum > 0">
		{vb:set initialCommentsPerPage, {vb:raw commentsPerPage}}
		{vb:set scrollToCommentFlag, 1}
	<vb:else />
		{vb:set pagenum, 1}
	</vb:if>
</vb:if>

<vb:if condition="!empty($conversation['textcount']) OR !empty($conversation['totalunpubcount'])">
	{vb:data nodes, node, listNodeContent, 
		{vb:raw conversation.nodeid}, 
		{vb:raw pagenum}, 
		{vb:raw initialCommentsPerPage}, 
		1, 
		null, 
		{vb:raw options}
	}
	<vb:comment>
		Keep these params in sync wiht listNodeContent call above and
		with vB5_Frontend_Controller_Ajax::actionPostComment()'s 
		listNodeFullContentCount() call.
	</vb:comment>
	{vb:data counts, node, listNodeFullContentCount, 
		{vb:raw conversation.nodeid}, 
		{vb:raw pagenum}, 
		{vb:raw initialCommentsPerPage}, 
		1, 
		null, 
		{vb:raw options}
	}
	{vb:set totalComments, {vb:raw counts.totalCount}}
</vb:if>

<div class="b-comments js-comments {vb:if "!empty($parentNodeIsBlog)", ' b-comments--blog js-comments--blog'} {vb:if "!empty($parentNodeIsArticle)", ' b-comments--article js-comments--article'} h-margin-bottom-xl h-clearfix<vb:if condition="!$totalComments"> h-hide</vb:if>">

	{vb:set totalPages, {vb:php ceil, {vb:math {vb:raw totalComments} / {vb:raw initialCommentsPerPage}}}}
	<vb:if condition="$totalPages > 0">
		{vb:set nodeCount, {vb:if "empty($nodes['errors'])", {vb:php count, {vb:raw nodes}}, 0}}
		<vb:if condition="$nodeCount > 0">
			{vb:set hasComments, 1}
			{vb:set commentIndex, {vb:raw totalComments}}
			<vb:if condition="$scrollToCommentFlag AND $pagenum > 1">
				{vb:set commentIndex, {vb:math {vb:var commentIndex} - {vb:math {vb:var pagenum} * {vb:var commentsPerPage}} + {vb:var commentsPerPage}}}
			<vb:else />
				{vb:set commentIndex, {vb:math {vb:var commentIndex} - {vb:var nodeCount} + 1}}
			</vb:if>




			<div class="b-comments__header js-comments__header b-media h-padding-vert-m h-padding-horiz-l">
				<div class="b-media__img--rev js-comments__pagination<vb:if condition="$totalComments <= $initialCommentsPerPage"> h-hide</vb:if>">
					<label>
						 <vb:if condition="$pagenum > 1 OR ($pagenum == 1 AND $nodeCount > 1)">
							{vb:set topIndexFrom, {vb:raw commentIndex}}
							{vb:set topIndexTo, {vb:math {vb:var topIndexFrom} + {vb:var commentsPerPage} - 1}}
							<vb:if condition="$commentsPerPage > $nodeCount">
								{vb:set topIndexTo, {vb:math {vb:var topIndexFrom} + {vb:var nodeCount} - 1}}
							</vb:if>
							<vb:if condition="$topIndexTo > $totalComments">
								{vb:set topIndexTo, {vb:raw totalComments}}
							</vb:if>
							<vb:if condition="$topIndexFrom == $topIndexTo">
								{vb:number {vb:raw topIndexFrom}}
							<vb:else />
								{vb:number {vb:raw topIndexFrom}} - {vb:number {vb:raw topIndexTo}}
							</vb:if>
						</vb:if>
					</label>
					<span class="b-comments__pagination--up js-comments__page--prev h-margin-left-s <vb:if condition="$pagenum == $totalPages"> h-hide-imp</vb:if>" title="{vb:phrase see_previous_comments}"<vb:if condition="$scrollToCommentFlag AND $pagenum < $totalPages"> data-page-num="{vb:math {vb:var pagenum} + 1}"</vb:if>><span class="b-icon b-icon__arrow-up-m"></span></span>
				</div>
				<div class="js-comments__total h-margin-left-l b-media__body">
					<span class="b-icon b-icon__speech-single h-margin-right-s"></span>
					<label class="js-comments__total-text h-align-top">
						<vb:if condition="$totalComments > 1">
							{vb:rawphrase x_comments_lower, {vb:number {vb:raw totalComments}}}
						<vb:else />
							{vb:rawphrase x_comment, {vb:number {vb:raw totalComments}}}
						</vb:if>
					</label>
				</div>
			</div>

			<vb:comment>
				The nodes need to be displayed in reversed order.
				The nodes should always be fetched in descending order but later when displaying (in certain condition such as this), 
				they need to be in ascending order.
			</vb:comment>
			{vb:set nodes, {vb:php array_reverse, {vb:raw nodes}, 1}}

			<ul class="b-comments__comments js-comments__comments h-left" {vb:schema itemprop, comment, UserComments}>

				{vb:set userIds, {vb:php array}}
				{vb:set userAvatarIds, {vb:php array}}
				<vb:each from="nodes" value="node">
					{vb:set num_elem, {vb:php array_push, {vb:raw userIds}, {vb:raw node.content.deleteuserid}}}
					{vb:set num_elem, {vb:php array_push, {vb:raw userAvatarIds}, {vb:raw node.content.userid}}}
				</vb:each>
				{vb:data userAvatars, user, fetchAvatars, {vb:raw userAvatarIds}, 1}

				<vb:each from="nodes" value="node">
					{vb:set hidePostIndex, {vb:if "$node['content']['unpublishdate'] OR !empty($hideCommentPostIndex)", 1, 0}}
					{vb:set avatarUserid, {vb:raw node.userid}}
					{vb:set userAvatar, {vb:if "!empty($userAvatars[$node['userid']])", {vb:raw userAvatars.$avatarUserid}, 0}}
					{vb:template conversation_comment_item, 
						conversation={vb:raw node.content}, 
						commentIndex={vb:raw commentIndex}, 
						postIndex={vb:raw postIndex}, 
						pagingInfo={vb:raw pagingInfo}, 
						hidePostIndex={vb:raw hidePostIndex}, 
						enableInlineMod={vb:raw enableInlineMod}, 
						parentNodeIsBlog={vb:raw parentNodeIsBlog}, 
						parentNodeIsArticle={vb:raw parentNodeIsArticle}, 
						avatar={vb:raw userAvatar}}

					{vb:set commentIndex, {vb:math {vb:var commentIndex} + 1}}
				</vb:each>

			</ul>


			<div class="b-comments__footer js-comments__footer b-media h-padding-vert-m h-padding-horiz-l h-clear<vb:if condition="!$scrollToCommentFlag OR $pagenum == 1"> h-hide</vb:if>">
				<div class="b-media__img--rev js-comments__pagination">
					<label>
						<vb:if condition="$scrollToCommentFlag AND $pagenum > 1">
							{vb:set bottomIndexFrom, {vb:math {vb:var topIndexTo} + 1}}
							{vb:set bottomIndexTo, {vb:math {vb:var bottomIndexFrom} + {vb:var commentsPerPage} - 1}}
							<vb:if condition="$bottomIndexTo > $totalComments">
								{vb:set bottomIndexTo, {vb:raw totalComments}}
							</vb:if>
							{vb:number {vb:raw bottomIndexFrom}} - {vb:number {vb:raw bottomIndexTo}}
						</vb:if>
					</label>
					<span class="b-comments__pagination--down js-comments__page--next h-margin-left-s" title="{vb:phrase see_next_comments}"<vb:if condition="isset($bottomIndexTo) AND $pagenum > 1"> data-page-num="{vb:math {vb:var pagenum} - 1}"</vb:if>><span class="b-icon b-icon__arrow-down-m"></span></span>
				</div>
			</div>

		</vb:if>
	</vb:if>

	<vb:if condition="!$hasComments">
		<div class="b-comments__header js-comments__header b-media h-padding-vert-m h-padding-horiz-l h-hide">
			<div class="js-comments__total h-margin-left-l b-media__body">
				<span class="b-icon b-icon__speech-single h-margin-right-s"></span>
				<label class="js-comments__total-text h-align-top"></label>
			</div>
			<div class="b-media__img--rev js-comments__pagination h-hide">
				<label></label>
				<span class="b-comments__pagination--up js-comments__page--prev h-margin-left-s" title="{vb:phrase see_previous_comments}"><span class="b-icon b-icon__arrow-up-m"></span></span>
			</div>
		</div>
		<ul class="b-comments__comments js-comments__comments h-left"></ul>
		<div class="b-comments__footer js-comments__footer b-media h-padding-vert-m h-padding-horiz-l h-clear h-hide">
			<div class="b-media__img--rev js-comments__pagination">
				<label></label>
				<span class="b-comments__pagination--down js-comments__page--next h-margin-left-s" title="{vb:phrase see_next_comments}"><span class="b-icon b-icon__arrow-down-m"></span></span>
			</div>
		</div>
	</vb:if>
</div>

