	{vb:strcat topicStatus, ' has-unpublished'}
</vb:if>
<vb:if condition="$topic['featured']">
	{vb:strcat topicStatus, ' featured'}
	<vb:if condition="$user['is_admin'] OR $user['is_supermod'] OR $user['is_moderator']">
		<vb:comment>Use a second status "highlight-featured" to do the
		highlighting, since we always add "featured", regardless of if
		we want the highlighting. We shouldn't change that, since people
		may be relying on it.</vb:comment>
		{vb:strcat topicStatus, ' highlight-featured'}
		<vb:if condition="!empty($topicPrefix)">
			{vb:strcat topicPrefix, ', '}
		</vb:if>
		{vb:strcat topicPrefix, {vb:phrase featured}}
	</vb:if>
</vb:if>
<vb:if condition="$topic['showpublished'] == 0">
	{vb:strcat topicStatus, ' deleted'}
