	<vb:if condition="$topic['unpublishdate'] > 0">
		{vb:strcat topicStatus, ' expiring-redirect'}
	</vb:if>
	<vb:if condition="!empty($topicPrefix)">
		{vb:strcat topicPrefix, ', '}
	</vb:if>
