		{vb:set showNode, 0}
	</vb:if>
<vb:else />
	{vb:set isRedirect, 0}
</vb:if>

<vb:if condition="!empty($topic['hasanswer'])">
	{vb:strcat topicStatus, ' answered'}
	<vb:if condition="$user['is_admin'] OR $user['is_supermod'] OR $user['is_moderator']">
		<vb:if condition="!empty($topicPrefix)">
			{vb:strcat topicPrefix, ', '}
		</vb:if>
		{vb:strcat topicPrefix, {vb:phrase answered}}
	</vb:if>
</vb:if>

