	{vb:strcat topicStatus, ' read'}
<vb:else />
	{vb:strcat topicStatus, ' unread'}
</vb:if>

<vb:if condition="!empty($topicPrefix)">
	{vb:strcat topicStatus, ' hasprefix'}
</vb:if>


