			********************** START REGULAR FORUMS **********************
			</vb:comment>


			<vb:comment> ***** TOPIC IMAGE CELL (AVATAR/TOPIC ICON) ***** </vb:comment>
			<td class="cell-topic-image js-full-height">
				<vb:if condition="$vboptions['threadmarking'] > 0 AND $user['userid'] > 0 AND !empty($readtime) AND $readtime < $lastcontent AND !$isRedirect">
					{vb:set newpostAvailable, 1}
					<vb:if condition="$iconTooltip != ''">
						{vb:strcat iconTooltip, ' '}
					</vb:if>
					{vb:strcat iconTooltip, {vb:phrase new_posts}}
				<vb:else />
					{vb:set newpostAvailable, 0}
				</vb:if>

				<vb:if condition="$vboptions['avatarintopiclist']">
					<div class="topic-avatar" title="{vb:raw iconTooltip}">
						{vb:template avatar,
							avataruser={vb:raw topic},
							avatar={vb:raw topic.avatar},
							title={vb:raw iconTooltip}}
					</div>
				<vb:else />
					<span class="vb-icon vb-icon-topic-status {vb:if "$enableInlineMod", "js-open-close-topic "}" title="{vb:raw iconTooltip}"></span>
				</vb:if>
			</td>

			<vb:comment> ***** TOPIC CELL ***** </vb:comment>
			<td class="cell-topic js-cell-topic">
				<div class="topic-wrapper js-topic-wrapper h-wordwrap">
					<vb:comment>The "go-to-first-unread" anchor</vb:comment>
					<vb:if condition="!empty($newpostAvailable)">
						{vb:set querystring.goto, 'newpost'}
						<a href="{vb:url {vb:raw topic.routeid}, {vb:raw topic}, {vb:raw querystring}}" class="go-to-first-unread b-icon b-icon__double-arrow-down--blue js-go-to-first-unread" title="{vb:phrase 'go_to_first_new_post'}">&nbsp;</a>
					</vb:if>
