							<vb:if condition="$topic['lastauthorid'] > 0">
								{vb:rawphrase by_x, 
									{vb:var profileUrl}, 
									{vb:raw topic.lastcontentauthor}, 
									{vb:var topic.lastauthorid}
								}
							<vb:else />
