					</vb:if>
					{vb:set qs.p, {vb:raw topic.lastcontentid}}
					<a href="{vb:if "!empty($topic['lastcontenturl'])", {vb:var topic.lastcontenturl}, {vb:url {vb:raw topic.routeid}, {vb:raw topic}, {vb:raw qs}}}" class="go-to-last-post b-icon b-icon__double-arrow-right2--blue" title="{vb:phrase 'go_to_last_post'}">&nbsp;</a>
