			<vb:if condition="$page['view'] == 'stream'">
				{vb:set sortOption.isstarter, 'ASC'}
				{vb:set sortOption.created, 'DESC'}
				{vb:set options.depth, 2}
				{vb:set options.view, 'conversation_stream'}
			<vb:elseif condition="$page['view'] == 'media'" />
				<vb:comment>we won't use the options for search. This is here just to avoid defaulting page.view to thread in the else.</vb:comment>
			<vb:else />
				<vb:if condition="$page['view'] != 'thread'">
					{vb:set page.view, 'thread'}
				</vb:if>
				{vb:set sortOption.isstarter, 'DESC'}
				{vb:set sortOption.created, 'ASC'}
				{vb:set options.depth, 1}
				{vb:set options.view, 'conversation_thread'}
				{vb:set options.nolimit, 1}
