			{vb:set options.include_starter, 1}
			{vb:set options.ignore_protected, 1}

			{vb:set searchPageParam, 1}
			<vb:if condition="empty($page['pagenum'])">
				{vb:set page.pagenum, 1}
			</vb:if>

