			{vb:headlink vb_module={vb:raw moduleName}, rel=next, href={vb:raw pagingInfo.nexturl}}
		</vb:if>
	</vb:if>

	<div class="b-module{vb:var widgetConfig.show_at_breakpoints_css_classes} canvas-widget default-widget conversation-content-widget <vb:if condition="$currentNodeIsBlog OR $currentNodeIsArticle"> blog-conversation-content-widget<vb:elseif condition="$currentNodeIsSG" /> group-conversation-content-widget widget-tabs<vb:else /> forum-conversation-content-widget widget-tabs</vb:if> widget-no-border widget-no-header-buttons {vb:raw topicStatus} axd-container<vb:if condition="$currentConversation['showopen'] == 0"> showclosed</vb:if><vb:if condition="$currentConversation['showopen'] == 0 AND !$currentConversation['canmoderate'] AND $currentConversation['open'] == 0"> closed</vb:if>" id="widget_{vb:raw widgetinstanceid}" data-widget-id="{vb:raw widgetid}" data-widget-instance-id="{vb:raw widgetinstanceid}"<vb:if condition="!empty($widgetinstanceid)"> data-widget-default-tab="{vb:raw widgetConfig.default_tab}"</vb:if>>
		<vb:if condition="empty($errorMessage)">
			<div class="conversation-status-messages">
				<vb:if condition="$currentConversation['showopen'] == 0">
					<div class="conversation-status-message notice close"><span>{vb:phrase this_topic_is_closed}</span><div class="close">X</div></div>
				</vb:if>
				<vb:if condition="$currentConversation['sticky']">
					<div class="conversation-status-message notice stick"><span>{vb:phrase this_is_a_sticky_topic}</span><div class="close">X</div></div>
				</vb:if>
				<vb:if condition="$enableInlineMod">
					<vb:if condition="$currentConversation['approved'] == 0">
						<div class="conversation-status-message notice approve"><span>{vb:phrase this_topic_is_unapproved}</span><div class="close">X</div></div>
					</vb:if>
					<vb:if condition="$currentConversation['unpublishdate']">
						<div class="conversation-status-message notice delete"><span>{vb:phrase this_topic_is_deleted}</span><div class="close">X</div></div>
					</vb:if>
					<vb:if condition="$currentConversation['featured']">
						<div class="conversation-status-message notice feature"><span>{vb:phrase this_topic_has_been_featured}</span><div class="close">X</div></div>
					</vb:if>
				</vb:if>
				<vb:if condition="!empty($currentConversation['hasanswer'])">
					<div class="conversation-status-message notice answered"><span>{vb:phrase topic_answered}</span><div class="close">X</div></div>
				</vb:if>
				<div class="conversation-status-message notice h-hide"><span></span><div class="close">X</div></div>
			</div>

