					<div class="conversation-content">
						<ul class="conversation-list list-container h-clearfix {vb:if $currentNodeIsBlog, blog-list} {vb:if $currentNodeIsArticle, article-list} <vb:if condition="$page['view'] == 'stream'">stream-view<vb:elseif condition="$page['view'] == 'thread'" />thread-view<vb:else />media-view</vb:if><vb:if condition="$currentNodeIsBlog AND $page['view'] == 'stream'"> stream-view-full-width</vb:if>">
							<vb:if condition="$page['view'] == 'stream'">
								{vb:set baseReplyTemplateName, 'display_contenttype_conversationreply_'}
								{vb:set starterTemplateName, {vb:raw baseReplyTemplateName}}
