									{vb:set tmpUserId, {vb:raw node.userid}}

									<vb:if condition="empty($parsedSignatures[$tmpUserId])">
										{vb:set nodes.$index.content.parsedSignature, ''}
									<vb:else />
										{vb:set nodes.$index.content.parsedSignature, {vb:raw parsedSignatures.$tmpUserId}}
									</vb:if>
