							</vb:if>

							{vb:set postIndex, -1}
							{vb:set counter, 0}
							<vb:each from="nodes" value="node">
								<vb:if condition="$node['content']['unpublishdate']">
									{vb:set hidePostIndex, 1}
								<vb:else />
									{vb:set hidePostIndex, 0}
									{vb:set postIndex, {vb:math {vb:raw postIndex} + 1}}
								</vb:if>
								<vb:if condition="$node['nodeid'] != $node['starter'] AND !$currentNodeIsBlog AND !$currentNodeIsArticle">
									<!-- Conversation Replies or Comments -->
									{vb:set replyTemplateName, {vb:raw baseReplyTemplateName}}
									{vb:strcat replyTemplateName, {vb:raw node.contenttypeclass}}
									<vb:if condition="!empty($page['pp']) AND intval($page['pp']) > 0 AND $page['p'] == $node['nodeid']">
										{vb:set commentId, {vb:raw page.pp}}
									<vb:else />
										{vb:set commentId, 0}
									</vb:if>

									<vb:if condition="$counter == $thispagetotal - 1">
										{vb:template {vb:raw replyTemplateName}, conversation={vb:raw node.content}, pagingInfo={vb:raw pagingInfo}, bbcodeOptions={vb:raw channelBbcodes}, postIndex={vb:raw postIndex}, commentId={vb:raw commentId}, showInlineMod={vb:raw showInlineMod}, widgetConfig={vb:raw widgetConfig}, view={vb:raw view}, hidePostIndex={vb:raw hidePostIndex}, adlocation="last-post"}
									<vb:else />
										{vb:template {vb:raw replyTemplateName}, conversation={vb:raw node.content}, pagingInfo={vb:raw pagingInfo}, bbcodeOptions={vb:raw channelBbcodes}, postIndex={vb:raw postIndex}, commentId={vb:raw commentId}, showInlineMod={vb:raw showInlineMod}, widgetConfig={vb:raw widgetConfig}, view={vb:raw view}, hidePostIndex={vb:raw hidePostIndex}}
									</vb:if>
								<vb:else />
									<vb:if condition="$node['nodeid'] == $node['starter']">
									<!-- Starter Conversation -->
									{vb:strcat starterTemplateName, {vb:raw node.contenttypeclass}}
									{vb:template {vb:raw starterTemplateName}, conversation={vb:raw node.content}, nodeid={vb:raw nodeid}, bbcodeOptions={vb:raw channelBbcodes}, postIndex=1, showInlineMod={vb:raw showInlineMod}, view={vb:raw view}, currentNodeIsBlog={vb:raw currentNodeIsBlog}, commentsPerPage={vb:raw widgetConfig.commentsPerPage}, initialCommentsPerPage={vb:raw widgetConfig.initialCommentsPerPage}, userCanCommentOnThisBlog={vb:raw userCanCommentOnThisBlog}, currentNodeIsArticle={vb:raw currentNodeIsArticle}, userCanCommentOnThisArticle={vb:raw userCanCommentOnThisArticle}, hidePostIndex={vb:raw hidePostIndex}, adlocation="first-post", pagingInfo={vb:raw pagingInfo}}
									</vb:if>
								</vb:if>
								{vb:set counter, {vb:math ({vb:var counter} + 1)}}

								<vb:comment>Insert answers "pinned" under the starter iff it's thread view</vb:comment>
								<vb:if condition="$page['view'] == 'thread' AND $node['nodeid'] == $node['starter']">
									{vb:data pinnedReplies, node, 'getPinnedRepliesFullContent', {vb:raw node}, {vb:raw options}}
									<vb:each from="pinnedReplies.nodes" value="pinnedReply">
										{vb:set replyTemplateName, {vb:raw baseReplyTemplateName}}
										{vb:strcat replyTemplateName, {vb:raw pinnedReply.contenttypeclass}}

										<vb:comment>
											Set up some passthru template controls.
											We have vars that we need to set up at this point, then pass along through 2 or 3
											levels of subtemplates. We should probably add a set of arrays specific for this
											purpose, but that requires touching a lot of templates, so sticking these into
											the "content" subarray for now which get passed down all the way as the 
											"conversation" parameter.
										</vb:comment>
										{vb:set templatehints, {vb:php array}}

										{vb:set threadviewHeaderControls, {vb:php array}}
										{vb:set threadviewHeaderControls.showAnswerLabel, 1}
										{vb:set threadviewHeaderControls.nopostanchor, 1}
										{vb:set threadviewHeaderControls.showgotopostlink, 1}
										{vb:set threadviewHeaderControls.additionalClasses, ' pinned-answer '}
										{vb:set templatehints.threadviewHeaderControls, {vb:raw threadviewHeaderControls}}

										{vb:set threadviewFooterControls, {vb:php array}}
										{vb:set footerControlsOverride, {vb:php array}}
										{vb:set footerControlsOverride.showEditCtrl, 0}
										{vb:set footerControlsOverride.showCommentCtrl, 0}
										{vb:set threadviewFooterControls.footerControlsOverride, {vb:raw footerControlsOverride}}
										{vb:set templatehints.threadviewFooterControls, {vb:raw threadviewFooterControls}}

										{vb:set pinnedReply.content.templatehints, {vb:raw templatehints}}

										{vb:template {vb:raw replyTemplateName}, 
											conversation={vb:raw pinnedReply.content}, 
											pagingInfo={vb:raw pagingInfo}, 
											bbcodeOptions={vb:raw channelBbcodes}, 
											hidePostIndex=1,
											hideCommentPostIndex=1,
											commentId=0, 
											showInlineMod={vb:raw showInlineMod}, 
											widgetConfig={vb:raw widgetConfig}, 
											view={vb:raw view}}
									</vb:each>
								</vb:if>
							</vb:each>
						</ul>
					</div>
				</div>
				<vb:if condition="!$currentNodeIsBlog AND !$currentNodeIsArticle">
					{vb:template pagenav, pagenav={vb:raw pagingInfo}}
					<vb:if condition="$pagingInfo['totalpages'] == 1">
						<div class="h-margin-top-xl"></div>
					</vb:if>
				</vb:if>

				<vb:if condition="$showContentEntry">
					{vb:data conversationRoute,
						route,
						getChannelConversationRoute,
						{vb:raw currentConversation.parentid}}

					{vb:hook 'conversation_above_entry'}
					{vb:template contententry,
						page={vb:raw page},
						parentid={vb:raw nodeid},
						routeid={vb:raw conversationRoute},
						pagenum={vb:raw pagingInfo.currentpage},
						conversation={vb:raw currentConversation},
						contentEntryType="reply",
						headerTitle={vb:raw headerTitle},
						submitButtonLabel={vb:phrase post_reply},
						allowTypeText=1,
						allowTypePhoto=1,
						allowTypeLink=1,
						allowTypePoll=0,
						showToggleEditor=1,
						showSmiley=1,
						showAttachment=1,
						showTags=0,
						showCancel=1,
						showTitle=0,
						isVm=0,
						initOnPageLoad=1,
						focusOnPageLoad=0,
						preloadJavascript=1,
						compactButtonSpacing=1}
				</vb:if>
			</div>
		<vb:else />
			<div class="widget-header h-clearfix">
				<div class="module-buttons">
					<span class="toggle-button module-button-item collapse" title="{vb:phrase collapse}" data-toggle-title="{vb:phrase expand}"><span class="b-icon b-icon__toggle-collapse">{vb:phrase collapse}</span></span>
				</div>
			</div>
			<div class="widget-content">
				<div class="conversation-view">
					{vb:template conversation_toolbar, position=top, showNewConversationBtn=1, floating=1}
					<div class="conversation-content">
						<ul class="thread-list list-container">
							<li class="h-left h-clear">{vb:phrase topic_not_found}</li>
						</ul>
					</div>
				</div>
				{vb:template pagenav, pagenav={vb:raw pagingInfo}}
			</div>
		</vb:if>
		{vb:hook 'conversation_below_entry'}
	</div>
	<script type="text/javascript">
	(function(){
	<vb:literal>
		window.vBulletin = window.vBulletin || {};
		vBulletin.widget = vBulletin.widget || {};
		vBulletin.conversation = vBulletin.conversation || {};
	</vb:literal>
		vBulletin.conversation.COMMENTS_PER_PAGE = Number('{vb:raw widgetConfig.commentsPerPage}') || 25;
		vBulletin.conversation.POSTS_PER_PAGE = Number('{vb:raw resultsPerPage}') || 15;
	})();
	</script>
<vb:else />
	<div class="conversation-empty">{vb:phrase no_permission}</div>
</vb:if>

