										{vb:set buttonType, 'subscribed'}
										{vb:set buttonLabel, 'following'}
										{vb:set buttonState, ' disabled="disabled"'}
									<vb:else />
										{vb:set buttonType, 'subscribe'}
										{vb:set buttonLabel, 'follow'}
									</vb:if>
									<button class="action_button {vb:raw buttonType}_button subscribe-btn b-button {vb:raw buttonCSS} h-right" data-userid="{vb:raw subscriber.userid}" id="add_{vb:raw subscriber.userid}"{vb:raw buttonState}>{vb:phrase {vb:raw buttonLabel}}</button>
								</vb:if>
								<div class="h-clear"></div>
							</li>
					</vb:each>
				</ul>
